import React, { Component } from "react";
import "./App.css";

import Person from "./Person/Person";

class App extends Component {
  state = {
    inputV: ""
  };

  inputChangeHandler = () => {
    this.setState({
      inputV: event.target.value
    });
  };

  render() {
    return (
      <div className="App">
        <input
          type="text"
          onChange={this.inputChangeHandler}
          value={this.state.inputV}
        />
      </div>
    );
  }
}

export default App;
