import React, {Component} from 'react';
import './App.css';

import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      {
        name: "Sonia",
        age: 1
      },
      {
        name: "SoniaJ",
        age: 2
      },
      {
        name: "SoniaJa",
        age: 3
      },
    ],
    other: "other value"
  }

  switchNameHandler = () => {
    this.setState({
      persons: [
        {
          name: "SoniaJain",
          age: 1
        },
        {
          name: "SoniaJ",
          age: 2
        },
        {
          name: "SoniaJa",
          age: 4
        },
      ]
    })
  }

  render() {
  return (
    <div className="App">
     <h1>React Project</h1>
     <p>This is really working!</p>
     <button onClick={this.switchNameHandler}>Switch Name</button>
     <Person name={this.state.persons[0].name} age={this.state.persons[0].age} />
     <Person name={this.state.persons[1].name} age={this.state.persons[1].age}><b>Hobbies::</b> ABCD </Person>
     <Person name={this.state.persons[2].name} age={this.state.persons[2].age} />
    </div>
  );
  }
}

export default App;
